import React, { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import './userHistory.css';

const UserHistory = () => {
  const [acceptedEvents, setAcceptedEvents] = useState([]);
  const user = JSON.parse(localStorage.getItem('loggedInUser'));

  useEffect(() => {
    if (user) {
      fetch(`http://localhost:3000/api/history/${user.id}`)
        .then(response => response.json())
        .then(data => {
          if (data) {
            setAcceptedEvents(data);
          }
        })
        .catch(error => console.error('Error fetching user history:', error));
    }
  }, [user]);

  const getEventDetails = (eventId) => {
    // Fetch event details from the server or use a pre-loaded event list
    // For simplicity, we'll assume the events list is available in the context
    const event = events.find(e => e.id === eventId);
    return event || {};
  };

  return (
    <div className="user-history-page">
      <br></br>
      <br></br>
      <h2>My Volunteer History</h2>
      <div className="table-container">
        <table>
          <thead>
            <tr>
              <th>Event Name</th>
              <th>Event Date</th>
              <th>Sign-Up Time</th>
            </tr>
          </thead>
          <tbody>
            {acceptedEvents.length > 0 ? (
              acceptedEvents.map((event, index) => {
                const eventDetails = getEventDetails(event.eventId);
                return (
                  <tr key={index}>
                    <td>{eventDetails.name}</td>
                    <td>{dayjs(eventDetails.date).format('MMMM D, YYYY')}</td>
                    <td>{dayjs(event.signUpTime).format('MMMM D, YYYY HH:mm:ss')}</td>
                  </tr>
                );
              })
            ) : (
              <tr>
                <td colSpan="3">No events signed up yet.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default UserHistory;
