import React, { useState, useEffect } from 'react';
import dayjs from 'dayjs';
import './matchingForm.css';

const VolunteerMatchingForm = () => {
  const [events, setEvents] = useState([]);
  const [skills, setSkills] = useState([]);
  const [selectedSkills, setSelectedSkills] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [selectedEvents, setSelectedEvents] = useState([]);
  const [matchingVolunteers, setMatchingVolunteers] = useState([]);

  useEffect(() => {
    // Fetch events from the server
    fetch('/api/volunteer-matching/events')
      .then(response => response.json())
      .then(data => setEvents(data))
      .catch(error => console.error('Error fetching events:', error));

    // Fetch skills from the server
    fetch('/api/volunteer-matching/skills')
      .then(response => response.json())
      .then(data => setSkills(data))
      .catch(error => console.error('Error fetching skills:', error));
  }, []);

  const handleSkillChange = (event) => {
    const selectedOptions = Array.from(event.target.selectedOptions).map(option => option.value);
    setSelectedSkills(selectedOptions);

    const filtered = events.filter(event =>
      selectedOptions.every(skill => event.requiredSkills.includes(skill))
    );
    setFilteredEvents(filtered);
  };

  const handleEventChange = (event) => {
    const selectedOptions = Array.from(event.target.selectedOptions).map(option => parseInt(option.value));
    setSelectedEvents(selectedOptions);

    fetch(`/api/volunteer-matching/match`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ eventIds: selectedOptions, skillIds: selectedSkills })
    })
      .then(response => response.json())
      .then(data => setMatchingVolunteers(data.volunteers))
      .catch(error => console.error('Error fetching matching volunteers:', error));
  };

  const getSkillNames = (skillIds) => {
    return skillIds.map(skillId => {
      const skill = skills.find(skill => skill.id === skillId);
      return skill ? skill.name : '';
    }).join(', ');
  };

  return (
    <div className="volunteer-matching-form">
      <div className="dropdown-container">
        <select multiple onChange={handleSkillChange}>
          {skills.map(skill => (
            <option key={skill.id} value={skill.id}>{skill.name}</option>
          ))}
        </select>
      </div>
      {filteredEvents.length > 0 && (
        <div className="dropdown-container">
          <select multiple onChange={handleEventChange}>
            {filteredEvents.map(event => (
              <option key={event.id} value={event.id}>{event.name}</option>
            ))}
          </select>
        </div>
      )}
      {matchingVolunteers.length > 0 && (
        <div>
          <h3>Matching Volunteers</h3>
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Name</th>
                  <th>Skills</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {matchingVolunteers.map(volunteer => (
                  <tr key={volunteer.id}>
                    <td>{volunteer.fullName}</td>
                    <td>{getSkillNames(volunteer.skills)}</td>
                    <td><button className="assign-button">Assign</button></td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default VolunteerMatchingForm;
